:root {
  --primary-color: #2c3942;
  --secondary-color: #1192ff;
  --tertiary-color: #997ac0;
  --button-bg: var(--tertiary-color);
  --material-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
  --screen-height: 568px;
  --screen-width: 320px;
}

* {
  &:before,
  &:after {
    box-sizing: inherit;
  }
}

html,
input {
  box-sizing: border-box;
}

button {
  appearance: none;
