webpack.config.js
 - this works including globally importing foundation scss.




     const path = require('path')
const webpack = require('webpack')
const HtmlWpPlugin = require('html-webpack-plugin')
const CleanWpPlugin = require('clean-webpack-plugin')
const ExtractWpPlugin = require('extract-text-webpack-plugin')
const autoprefixer = require('autoprefixer')
const atl = require('awesome-typescript-loader')

module.exports = {
  entry: {
    app: path.resolve(__dirname, 'src/js/app.js')
  },

  output: {
    path: path.resolve(__dirname, 'dist'),
    filename: '[name].bundle.js',
    sourceMapFilename: '[name].bundle.map',
    pathinfo: true
  },
  resolve: {
    extensions: ['.ts', '.js', '.vue', '.json'],
    alias: {
      'vue$': 'vue/dist/vue.esm.js'
    }
  },
  devtool: 'cheap-module-source-map',
  plugins: [
    new HtmlWpPlugin({
      title: 'Learning Vue',
      template: 'src/index.html',
      filename: 'index.html',
      inject: true
    }),
    new CleanWpPlugin([path.resolve(__dirname, 'dist')]),
    new ExtractWpPlugin({
      filename: 'styles.css',
      allChunks: true
    }),
    new webpack.HotModuleReplacementPlugin(),
    new atl.CheckerPlugin()
  ],
  module: {
    rules: [
      {
        test: /\.js$/,
        use: {
          loader: 'babel-loader',
          options: {
            presets: [
              ['env',
                {
                  targets: {
                    'browsers': ['last 2 versions', 'ie>=7']
                  }
                }
              ]
            ]
          }
        }
      },
      {
        test: /\.css$/,
        loaders: ExtractWpPlugin.extract([
          'style-loader/url!file-loader',
          'css-loader'
        ])
      },
      {
        test: /\.ts$/,
        use: 'awesome-typescript-loader'
      },
      {
        test: /\.vue$/,
        loader: 'vue-loader',
        options: {
          loaders: {
            scss: ['vue-style-loader', {
              loader: 'css-loader',
              options: {
                minimize: false,
                sourceMap: false
              }
            },
            {
              loader: 'sass-loader',
              options:
              {
                includePaths: ['./src/styles'],
                data: '@import "./src/styles/app";',
                sourceMap: false
              }
            }],
            ts: 'awesome-typescript-loader'
          },
          extractCSS: false
        }
      },
      {
        test: /\.scss$/,
        use: ExtractWpPlugin.extract({
          fallback: 'style-loader',
          use: [
            {
              loader: 'sass-loader',
              options:
            {
              includePaths: ['./src/styles'],
              data: '@import "./src/styles/app";',
              sourceMap: false
            }
            },
            'css-loader',
            {
              loader: 'postcss-loader',
              options:
                  {
                    plugins: function () {
                      return [autoprefixer]
                    }
                  }
            }
          ]
        }
        )
      },
      {
        test: /\.(woff2?|eot|ttf|otf)(\?.*)?$/,
        loader: 'url-loader',
        query: {
          limit: 10000,
          name: 'fonts/[name].[hash:7].[ext]'
        }
      },
      {
        test: /\.(png|svg|jpg|gif)$/,
        use: [
          'file-loader?name=images/[name].[ext]'
        ]
      }
    ]
  },
  devServer: {
    port: 8008,
    historyApiFallback: true,
    open: false,
    inline: true
  }
}







