Using  json-server  Setup  Create directory -  test_server  touch server.js  - server startup file  npm init  - create config file. Set  server.js  as the start script.  Create  server.js  file.  npm install faker json-server --save-dev         //server.js
    const jsonServer = require('json-server')
    const customers = require('./customers')
    const server = jsonServer.create()
    const router = jsonServer.router(customers())
    const middlewares = jsonServer.defaults()

    server.use(middlewares)
    server.use(router)
    server.listen(3000, () => {
      console.log('JSON Server is running')
    })

    //customer.js
    var faker = require('faker')
    function generateCustomers () {
        var customers = []
        for (var id = 0; id < 50; id++) {

        var firstName = faker.name.firstName();
        var lastName = faker.name.lastName();
        var phoneNumber = faker.phone.phoneNumberFormat();

        customers.push({
            "id": id,
            "first_name": firstName,
            "last_name": lastName,
            "phone": phoneNumber
        })
    }
    return { "customers": customers}
    }
    module.exports = generateCustomers

